'''

Give a string s, count the number of non-empty (contiguous) substrings that have the same number of 0's and 1's,
and all the 0's and all the 1's in these substrings are grouped consecutively.

Substrings that occur multiple times are counted the number of times they occur.
'''


class Solution:
    def countBinarySubstrings(self, s: str) -> int:
        n = len(s)
        i = 0
        final_count = 0
        print('n', n)
        while i < n:
            print('i', i)
            j = i + 1
            zero_count = 1 if s[i] == '0' else 0
            one_count = 1 if s[i] == '1' else 0
            flag = False
            while j < n and s[j] == '0' and s[i] == '0' and not flag:
                zero_count += 1
                j += 1
            while j < n and s[j] == '1' and s[i] == '1' and not flag:
                one_count += 1
                j += 1

            while j < n and s[i] == '0' and s[j] == '1' and not flag:
                one_count += 1
                j += 1
                if one_count == zero_count:
                    flag = True
                    break

            while j < n and s[i] == '1' and s[j] == '0' and not flag:
                zero_count += 1
                j += 1
                if one_count == zero_count:
                    flag = True
                    break

            if flag:
                final_count += 1
            i += 1

        return final_count

    def countBinarySubstringsOptimized(self, s):
        n = len(s)
        curr, prev, final_ans = 1, 0, 0
        for i in range(1, n):
            if s[i] == s[i - 1]:
                curr += 1
            else:
                final_ans += min(curr, prev)
                prev, curr = curr, 1
        return final_ans


print(Solution().countBinarySubstringsOptimized(
    ""))
